document.addEventListener("DOMContentLoaded", () => {
    const instance = new Mark(document.querySelector(".main-container"));
    const terms = Object.keys(glossary_terms);

    instance.mark(terms, {
        element: "span",
        className: "glossary-term",
        separateWordSearch: false,
        caseSensitive: false, 
        each: function(node) {
            console.log(node);
            const text = node.textContent;
            const matchedKey = terms.find(key => text.toLowerCase() === key.toLowerCase());
            if (matchedKey) {
                node.setAttribute("data-tippy-content", glossary_terms[matchedKey]);
            }
        },
        done: function(counter){
            console.log("Total marks:", counter);
            tippy('.glossary-term', {
                theme: 'light',
                animation: 'shift-away',
                delay: [100, 0],
                maxWidth: 300,
            });
        }
    });
    
});